import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredExpenses, setFilteredExpenses] = useState(props.expenses);
  const filterByYear = (year) => {
    setFilteredExpenses((prevExpenses) => {
      prevExpenses.filter((expense) => {
        console.log(expense.expenseDate.getFullYear());
        return expense.expenseDate.getFullYear() === year ? expense : null;
      });
    });
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onChangeFilterByYear={filterByYear} />
        {filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            expenseDate={expense.expenseDate}
            expenseTitle={expense.expenseTitle}
            expenseAmount={expense.expenseAmount}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
